require_once('workflows.php');
$w = new Workflows();

$url = "http://inews.io/api/alfred/latest";
$content = $w->request( $url );
$content_items = json_decode( $content );

function digg_count($count) {
  if($count == '0' ) {
    $icon = 'digg_0.png';
  }
  elseif( $count == '1' ) {
    $icon = 'digg_1.png';
  }
  elseif( $count > '1' ) {
    $icon = 'digg_1+.png';
  }
  elseif( $count > '10' ) {
    $icon = 'digg_10+.png';
  }
  elseif( $count > '20' ) {
    $icon = 'digg_20+.png';
  }
  elseif( $count > '50' ) {
    $icon = 'digg_50+.png';
  }
  elseif( $count > '100' ) {
    $icon = 'digg_100+.png';
  }
  return $icon;
}

foreach($content_items as $item ):
 	$w->result( $item->id, $item->link, $item->title, $item->content, digg_count($item->digg_count) );
endforeach;

$results = $w->results();
if ( count( $results ) == 0 ):
  $w->result( 'inews', 'http://inews.io', 'No News Found.', 'Please visit the inews.io to see what happend :(', 'icon.png' );
endif;

echo $w->toxml();

